import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r as s,o as i,c as a,a as e,b as t,d as r,e as p}from"./app-6d2325bd.js";const l={},c={href:"https://githuub.com/yzsong06",target:"_blank",rel:"noopener noreferrer"},_={href:"https://github.com/MuFeng086",target:"_blank",rel:"noopener noreferrer"},h={href:"https://github.com/TakagiWikiSitesCN/Hexo-TakagiWiki",target:"_blank",rel:"noopener noreferrer"},u={href:"https://github.com/TakagiWikiSitesCN/VuePress-TakagiWiki",target:"_blank",rel:"noopener noreferrer"},d=p('<p>站点建设速度目前来看并不快，主要也是我们这个项目其实人也不多（目前主要就我跟牧丰和南栀），而且在分工上基本就是我管站点框架功能类，牧丰可能比较全能（Doge）、而南栀主要只是写内容。主要也是因为我并不是很擅长写文档内容。</p><p>那么前面也说了这样一堆，也是希望能够吸引更多的高木厨们，为本项目做出一点贡献，任何意义上的支持都可以，哪怕是指出站点上的不完善之处或者错误，再或者为文档增加或修改些许内容，都是对本项目的最大贡献。</p><p>我们也将会在以后专门设立的贡献者专页中专门答谢各位对本项目做出的贡献！</p><p>不过我也知道，很多人也表示是心有余而力不足，比方说不会本项目文档所依赖的Markdown语法，或者网络环境无法使用，或不会使用Github等等...</p><p>这些都没有关系，今天我写这个专页也是为了帮助你为本项目做出贡献，我会在这里解答一些常见问题或者部分使用方法，这个分类也将会持续进行更新。</p><p><strong>如果并没有足够把握，更推荐大家在 issues（议题）或 discussions（讨论）中提出自己的想法，而不是冒着很可能需要多次修修补补的风险去编辑源文件。这可能会因内容问题造成多次无谓的部署和更新。同时，由于部署程序在单位时间内的重部署次数有限，一次提交就会消耗一次，短时间重复性提交小幅修改对我们也不是个好事。</strong></p><p><strong>所有对此项目作出贡献的朋友，都会在未来设置的贡献者专页中展示。</strong></p><h2 id="编辑指引" tabindex="-1"><a class="header-anchor" href="#编辑指引" aria-hidden="true">#</a> 编辑指引</h2><p><strong>1. 不同编辑者身份</strong></p><p>此处我们需要分两种分情况：<strong>短期/少量编辑者</strong>（适用于看到问题顺带帮忙解决的）、<strong>长期编辑者/合作者</strong>（可多次帮忙完善或提供意见建议者）</p><p>1.1 短期编辑者</p><p>由于我们对于数据内容稳定性的要求，对于短期编辑者我们无法给予便捷的直接编辑源仓库文档的权限，请按照以下步骤进行：</p>',12),g={href:"https://github.com/TakagiWikiSitesCN/VuePress-TakagiWiki",target:"_blank",rel:"noopener noreferrer"},k=e("p",null,"1.2 长期编辑者",-1),f=e("p",null,"如果需要长期多次修改，可联系本站或仓库管理员申请将你拉入协作者，加入协作者身份后，您可直接对仓库内容进行更改无需通过拉去提交等较为复杂和低时效性的流程。 但因为协作者权限可直接修改全部内容的权限所在，因此对于权限的放行我们持谨慎态度，敬请理解。",-1),b=e("p",null,[e("strong",null,"2. 如何编辑")],-1),m=e("p",null,"文档全部兼容Markdown语法，请尊重其他现有界面的布局风格，以客观公开可见可查的内容为基础进行内容填充。",-1),x=e("p",null,"文档位置：【例】“音乐”相关页面，则文档文件存放在 /music，编辑内部相应名称文档以执行更改",-1),N=e("p",null,"其余并没有什么严格规定~",-1),T=e("hr",null,null,-1);function V(S,W){const o=s("ExternalLinkIcon");return i(),a("div",null,[e("p",null,[t("Hi,我是"),e("a",c,[t("一只鬆"),r(o)]),t("，这个资料站项目建立有一段时间了，它的雏形项目最早则是由"),e("a",_,[t("牧丰（轻虹空雨)"),r(o)]),t(" 所发起建立的基于Hexo框架的自建资料站项目（"),e("a",h,[t("Hexo站点Github仓库"),r(o)]),t("），在经过约半年的建设后，在我的建议与亲自操刀改进下，目前更换为了基于Vuepress的资料站（"),e("a",u,[t("本站点仓库"),r(o)]),t("）。")]),d,e("p",null,[t("请将站点托管的Github仓库 "),e("a",g,[t("点击跳转"),r(o)]),t(" fork（复刻）至你的Github账号下，在复课后的个人仓库内自主编辑。编辑完成后点击“关于”栏左侧的contribute（贡献）按钮，点击open pull request（提交拉取请求），编辑好本次提交的简介，可在右侧选择受理人和本次编辑内容的类型。提交完成后，源仓库这边会收到拉取请求，您只要等待这边确认合并分支。您提交的分支内容将于源仓库主分支合并并自动触发网站重部署，这样一次内容编辑就完成了。")]),k,f,b,m,x,N,T])}const H=n(l,[["render",V],["__file","Start.html.vue"]]);export{H as default};
